package _001_variables_basics._variable_syntax_types;

public class Variables_Syntax_2
{

	public Variables_Syntax_2()
	{
		// TODO Auto-generated constructor stub
	}

	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub

		/*                                    *
         *  Syntax : datatype variable_name;  *
         *           variable_name = value;   *
         *                                    */
		
		String Name;
		int Age;
		boolean is_tall;
		char Rank;
		
		Name = "Goku";
		Age = 100;
		is_tall = true;
		Rank = 'S';
		

		System.out.println(Name);
		System.out.println(Age);
		System.out.println(Rank);
		System.out.println(is_tall);
	}

}
