package _004_Looping_Statements_in_JAVA._4_do_while_Loop;

public class _Print_0_to_5_using_do_while_Loop 
{

	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
		int i = 0;
		
		do 
		{
			System.out.println(i);
			
			i++;
			
		} while (i <= 5);
	}

}
