package _007_Arrays_Concept._1_One_Dimensional_Array._basic_Array_creation_Method_2;

public class Array_of_Chars 
{

	public Array_of_Chars() 
	{
		// TODO Auto-generated constructor stub
	}

	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub

		char[] letters = new char[6];
		
		letters[0] = '3';
		letters[1] = 'A';
		letters[4] = 'g';
		letters[3] = '+';
		letters[2] = ' ';
		letters[5] = 100;
		
		System.out.println(letters[0]);
		System.out.println(letters[1]);
		System.out.println(letters[2]);
		System.out.println(letters[3]);
		System.out.println(letters[4]);
		System.out.println(letters[5]);
	}

}
