package _008_Object_class_Concept._objects;

public class _basic_Object_variable 
{

	public _basic_Object_variable() 
	{
		// TODO Auto-generated constructor stub
	}

	public static void main(String[] args) 
	{

		Object v1; /* can accept any type of input  */
		
		
		v1 = "saran";
		System.out.println(v1);
		
		v1 = 'A';
		System.out.println(v1);
		
		v1 = 100;
		System.out.println(v1);
		
		v1 = 99.99f;
		System.out.println(v1);
		
		v1 = 99.9d;
		System.out.println(v1);
		
		v1 = true;
		System.out.println(v1);
	}

}
